var UserDashboard={defaults:{divFormPost:"#post_article",divResultRecherche:".users-result",divSuggestArticles:"#suggest_articles"},init:function(e){this.params=$.extend(this.defaults,e)},getNewPosts:function(){var e=$("section#zone_post :first-child").data("postid");$.ajax({url:"dashboard/getNewPosts",type:"get",data:"recentId="+e}).success(function(e){console.log(e);e!=0&&$("#zone_post").prepend(e)})},post:function(){$.ajax({url:"dashboard/user/post",type:"post",data:$(UserDashboard.params.divFormPost).serialize()}).success(function(e){UserDashboard.getNewPosts();User.getUserData()})},getSuggestArticles:function(){$.ajax({url:"dashboard/getSuggestArticles",method:"get"}).success(function(e){$(UserDashboard.params.divSuggestArticles).html(e)})},getSuggestFollow:function(){},searchUser:function(e){$.ajax({url:e.attr("action"),method:e.attr("method"),data:e.serialize()}).success(function(e){$(UserDashboard.params.divResultRecherche).html(e);console.log(e)})}};